package sunb.education.habbit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import sunb.education.habbit.components.Rule;import sunb.education.habbit.services.DatabaseService;import java.util.List;import java.util.Map;import java.util.Objects;/** * Created with IntelliJ IDEA. * Description: * Project Name:    habbit * User:            sunb * Date:            2017-11-28 * Time:            14:49 */@RestControllerpublic class RuleController {    @Autowired    DatabaseService databaseService;    @PostMapping("rule")    public Rule addRule(@RequestBody Map<String, String> jsonMap) {        String text = Objects.requireNonNull(jsonMap.get("text"));        return databaseService.addRule(text);    }    @GetMapping("rules")    public List<Rule> rules() {        return databaseService.lsRules();    }}